import * as React from 'react';
import { styled } from '@mui/material/styles';
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import Collapse from '@mui/material/Collapse';
import Avatar from '@mui/material/Avatar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import { red } from '@mui/material/colors';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import InsertCommentIcon from '@mui/icons-material/InsertComment';
import { Link } from "react-router-dom";
import OutlinedInput from '@mui/material/OutlinedInput';
import Button from '@mui/material/Button';
import { InputAdornment } from '@mui/material';
import  { useState, useEffect } from "react";
//import { useState } from "react";



const ExpandMore = styled((props) => {
    const { expand, ...other } = props;
    return <IconButton {...other} />;
})(({ theme, expand }) => ({
    transform: !expand ? 'rotate(0deg)' : '',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
        duration: theme.transitions.duration.shortest,
    }),
}));


export default function PostForm(props) {
    const [text,setText]=useState("");
    const [title,setTitle]=React.useState("");
   
    const { kisiId, kisiName,refreshPosts} = props;
    const [isSent,setIsSent]=useState(false); //post isteği gönderdik mi database e onun için yazıyoruz

    const savePost=()=>{
        fetch("/posts",
        {
            method:"POST",
            headers:{
                "Content-Type":"application/json",
            },
            body:JSON.stringify({
                title:title,
                kisiId:kisiId,
                text:text,
            }),
        })
        .then((res) => res.json())
        .catch((err) => console.log("error"))
    }

    const handleSubmit = () => {
        savePost();
        setIsSent(true);
        setTitle("");
        setText("");
        refreshPosts();
    }
    //handleTitle,handleText input alanına birşeyler girmeye  başladığımda çağrılan fonksiyonlardır
    const handleTitle=(value)=>{
        setTitle(value);
        setIsSent(false);
    }
    const handleText=(value)=>{
        setText(value);
        setIsSent(false);
    }

    //const classes = ExpandMore();




    return (
        <div className="postContainer">
            <Card sx={{ width: 800, textAlign: 'left', margin: 2 }}>
                <CardHeader
                    avatar={
                        <Link to={{ pathname: '/kisiler/' + kisiId }} sx={{ textDecoration: "none", boxShadow: "none", color: "white" }}>
                            <Avatar sx={{ background:'linear-gradient(45deg,#2196F3 30%, #21CBF3 90%)' }} aria-label="recipe">
                                {kisiName.charAt(0).toUpperCase()}
                            </Avatar>
                        </Link>
                    }

                    title={<OutlinedInput


                        id="outlined-password-input"
                        multiline
                        label="Password"
                        type="password"
                        autoComplete="current-password"
                        inputProps={{ maxLength: 25 }}
                        fullWidth
                        value={title}
                        onChange={(i)=>handleTitle(i.target.value)}
                    >

                    </OutlinedInput>}

                />

                <CardContent>
                    <Typography variant="body2" color="text.secondary">
                        <OutlinedInput
                            id="outlined-password-input"
                            multiline
                            label="Passwordmerhaba"
                            type="password"
                            autoComplete="current-password"
                            inputProps={{ maxLength: 250 }}
                            fullWidth
                            value={text}
                            onChange={(i)=>handleText(i.target.value)}
                            endAdornment={
                                <InputAdornment position="end">
                                    <Button
                                    variant="contained"
                                    style={{background:'linear-gradient(45deg,#2196F3 30%, #21CBF3 90%)',
                                    color:'white'}}
                                    onClick={handleSubmit}
                                    >Post</Button>
                                </InputAdornment>
                            }
                        >

                        </OutlinedInput>
                    </Typography>
                </CardContent>
              
            </Card>

        </div>
    )
}
