import React from 'react';
import Post from '../Post/Post';
import  { useState, useEffect } from "react";
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';

function Home() {
    const [error, setError] = useState(null)
    const [isLoaded, setIsLoaded] = useState(false) //data geldi gelmedi mi
    const [postList, setPostList] = useState([]) //data bunun içine dolacak


    useEffect(() => {
        fetch("/posts")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true); //data geldi
                    setPostList(result) //data
                },
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }
            )

    }, [])
    if (error) //en başta null olarak gönderildiği için buraya girmez parametre error olduğunda girer
    {
        return <div>  Error !!! </div>;
    } else if (!isLoaded) //data henüz gelemdiyse
    {
        return <div>Loading... </div>;

    }
    else {
        return (



            <Container fixed Box sx={{   rowLength: 100 , display:"flex", flexWrap:"wrap", justifyContent:"center", alignItems:"center", backgroundColor:'#f0f5ff'}}>
                 {postList.map(post => (
                    <Post kisiId={post.kisiId} kisiName={post.kisiName} title= {post.title}  text={post.text}></Post> //gelen her bir datayı post componenti olarak renderlar
                    
                ))}
            {/* <Box sx={{ display:"flex",flexWrap:"wrap",justifyContent:"center",alignItems:"center",backgroundColor:'#cfe8fc', height:'100vh'}} /> */}
            {/* {postList.map(post => (
                    <Post title= {post.title}  text={post.text}></Post> //gelen her bir datayı post componenti olarak renderlar
                    
                ))} */}
          </Container>






          
        );
    }
}

    export default Home;
