import * as React from 'react';
import { styled } from '@mui/material/styles';
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import Collapse from '@mui/material/Collapse';
import Avatar from '@mui/material/Avatar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import { red } from '@mui/material/colors';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import InsertCommentIcon from '@mui/icons-material/InsertComment';
import { Link } from "react-router-dom";
import OutlinedInput from '@mui/material/OutlinedInput';
//import { useState } from "react";



const ExpandMore = styled((props) => {
    const { expand, ...other } = props;
    return <IconButton {...other} />;
})(({ theme, expand }) => ({
    transform: !expand ? 'rotate(0deg)' : '',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
        duration: theme.transitions.duration.shortest,
    }),
}));


export default function PostForm(props) {
    const [expanded, setExpanded] = React.useState(false); //aşağı doğru açılıp kapanan ok için

    const handleExpandClick = () => {
        setExpanded(!expanded);
    };
    const { title, text, kisiId, kisiName } = props;
    const [liked, setLiked] = React.useState(false);

    const handleLike = () => {
        setLiked(!liked);
    }

    //const classes = ExpandMore();




    return (
        <div className="postContainer">
            <Card sx={{ width: 800, textAlign: 'left', margin: 2 }}>
                <CardHeader
                    avatar={
                        <Link to={{ pathname: '/kisiler/' + kisiId }} sx={{ textDecoration: "none", boxShadow: "none", color: "white" }}>
                            <Avatar sx={{ bgcolor: red[500] }} aria-label="recipe">
                                {kisiName.charAt(0).toUpperCase()}
                            </Avatar>
                        </Link>
                    }

                    title={<OutlinedInput


                        id="outlined-password-input"
                        multiline
                        label="Passwordmerhaba"
                        type="password"
                        autoComplete="current-password"
                        inputProps={{ maxLength: 25 }}
                        fullWidth
                    >

                    </OutlinedInput>}

                />

                <CardContent>
                    <Typography variant="body2" color="text.secondary">
                        <OutlinedInput
                            id="outlined-password-input"
                            multiline
                            label="Passwordmerhaba"
                            type="password"
                            autoComplete="current-password"
                            inputProps={{ maxLength: 250 }}
                            fullWidth
                        >

                        </OutlinedInput>
                    </Typography>
                </CardContent>
                <CardActions disableSpacing>
                    <IconButton

                        onClick={handleLike}
                        aria-label="add to favorites">
                        <FavoriteIcon style={liked ? { color: 'red' } : null} />
                    </IconButton>

                    <ExpandMore
                        expand={expanded}
                        onClick={handleExpandClick}
                        aria-expanded={expanded}
                        aria-label="show more"
                    >
                        <InsertCommentIcon />
                    </ExpandMore>
                </CardActions>
                <Collapse in={expanded} timeout="auto" unmountOnExit>
                    <CardContent>

                    </CardContent>
                </Collapse>
            </Card>

        </div>
    )
}
